INSERT IGNORE INTO `l_talk` (`id`, `uid`, `head`, `nickname`, `province`, `city`, `ip`, `content`, `create_time`, `is_show`, `status`, `expire_time`, `laud`) VALUES
	(62, 1, NULL, '遛遛', '四川', '成都', '171.214.181.59', '<p><span style=\\"font-size: 14px;\\">在正常交流中掺杂编程规范和语法，编程语言都有自己特定的语法，程序员在开发者都要遵守这些。当然，还有一些约定俗成的编程规范，如代码缩进（是缩进4个字符还是8个字符）、注释风格，采用骆驼式（CamelCase）或帕斯卡式（Pascal）来命名变量或函数等等。</span><br/></p>', 1460986073, 1, 0, 0, 3),
	(63, 1, NULL, '水蓝', '四川', '成都', '101.204.148.129', '<p><span id=\\"text110\\">一个同事站在楼顶威胁着说自己要跳下来，我赶紧找到警察，询问能否让我和他说两句话。<br/>警察说：“我们已经有专门负责谈判的警员在和他沟通了，您最好不要上去。”<br/>没办法，我只好在下面冲他喊了：“小日，我是你同事小月啊，你能不能稍微向一边挪一下，不然跳下来会砸到我车的呀。。。”</span></p>', 1460949364, 1, 0, 0, 11),
	(69, 1, NULL, '梵哲希', '四川', '成都', '101.204.148.129', '<p>上体育课，老师叫我们练柔韧度，练就练吧，居然叫我们一字马，gg。女老师先跟我们示范了一遍，然后叫我们腿伸开，妈的腿疼死了。我对老师说，只能劈到这\r\n了，老师说：我来帮你，我说：你有好方法早不说，快点。老师腿一伸勾住我的脚，只听嗤的一声我的裤裆叉开了，今天我挂档上体育课的！！<br/></p>', 1460970444, 1, 0, 0, 3),
	(65, 1, NULL, '牛儿不吃草', '四川', '成都', '101.204.148.129', '<p>话说湖南有个小伙儿叫小杨，谈了个女朋友叫小袁，后来又认识了小游，故而劈腿跟小游走到了一起。但好景不长，小杨和小游也分手了，又回去找小袁求复合，小袁不同意，于是乎小杨就带着小袁去抢劫小游，索要5万补偿费。</p>', 1460957250, 1, 0, 0, 2),
	(67, 1, NULL, '梵哲希', '四川', '成都', '171.214.181.59', '<p>&nbsp;小李家里有三亩薄地，种了三年，小李现在开上了宝马，朋友不理解，哪里来的钱<br/>&nbsp;朋友：“你小子，哪来的钱?”<br/>&nbsp;小李：“前年种了三亩姜赚一笔，去年种了三亩小米，又赚一笔，今年又种了三亩大葱，又他妈赚一笔，看来还是种地好!”</p>', 1460986010, 1, 0, 0, 2),
	(68, 1, NULL, '山上一奇葩', '四川', '成都', '171.214.181.59', '<p>科比退役了，你们觉得欠他一场球赛。星爷不演了，你们觉得欠他一张电影票。快播王欣判刑了，你们觉得欠他一个会。<br/>不要等我有对象了，你们才后悔没找我处对象。人生没有后悔药，千金难买早知道.</p>', 1460986021, 1, 0, 0, 1),
	(70, 1, NULL, '茅庐', '四川', '成都', '101.204.148.129', '<p>前天跟着业主去收楼验房！业主觉得房子层高太低，吊顶后层高更低！我就建议他装修成田园风格，可以不用吊顶，贴石膏线条就行！业主听了直翻白眼：我在农村住了几十年田园风格，好不容易买套房，你还叫我装田园风格，我才不干……</p>', 1460970463, 1, 0, 0, 1),
	(71, 1, NULL, '梵哲希', '四川', '成都', '101.204.148.129', '<p>老家隔壁四十多岁的单身汉陈叔，科目二通过，大摆50桌，好吧，之前随的份子钱都回来了！哈哈呵呵……</p>', 1460970510, 1, 0, 0, 4),
	(72, 1, NULL, '毛妮子', '四川', '成都', '101.204.150.191', '<p>在一个面馆吃饭，不一会进来一个穿着邋遢的人，问老板能不能给碗面吃？钱包丢了，回不去家了，现在特别饿，老板二话没说，转身走进了厨房，不一会端来一碗\r\n面，他狼吞虎咽的就吃了起来，吃完后老板给他三百块钱，当时店里的人都说老板上当了，这种人都是骗子云云的，老板只说了一句话，刚才那碗面我没放盐。</p>', 1461058260, 1, 0, 0, 2),
	(73, 1, NULL, '阑尾炎', '四川', '成都', '101.204.150.191', '<p>一个人身边的位置只有那麽多,你能给的也只有那麽多,在这个狭小的圈子里,有些人要进来,就有一些人不得不离开。</p>', 1461058586, 1, 0, 0, 2),
	(79, 0, 'http://q.qlogo.cn/qqapp/101299164/33A82EAE60446EA22A4ACD6158DCF0E5/100', '指尖一缕年华', '四川', '成都', '101.204.151.26', '忙碌是一种幸福，让我们没时间体会痛苦；奔波是一种快乐，让我们真实地感受生活；疲惫是一种享受，让我们无暇空虚。', 1461122879, 1, 0, 1461122999, 2),
	(78, 1, NULL, '孙猴子', '四川', '成都', '101.204.151.26', '<p>嫂子烫了个卷发，回到家后，小侄女一个劲的对着嫂子头发拨来拨去的看~<br/>我说:“小丫头，你看什么呐？”<br/>侄女说:“哦，我就是看看能不能在鸡窝里找出鸡蛋。。。”</p>', 1461122793, 1, 0, 0, 2),
	(80, 0, 'http://tp1.sinaimg.cn/5878554056/180/5752988239/1', '撩人慵懒', '四川', '成都', '101.204.150.53', '看到嫂子在打孩子，我心急的问：孩子犯什么错了？嫂子说：这熊孩子非要吃糖葫芦。我说：不就吃糖葫芦吗，为啥打这么狠？嫂子生气的说：你不知道啊，他非要吃肉...', 1461215936, 1, 0, 1461216056, 11),
	(81, 0, 'http://tp1.sinaimg.cn/5878554056/180/5752988239/1', '撩人慵懒', '四川', '成都', '101.204.150.53', '听到楼下两口子在吵架，家里的熊孩子一边哭一边嚷道：粑粑，你不要打妈妈...不要打妈妈了，你是打不过她的...', 1461216491, 1, 0, 1461216611, 22),
	(82, 0, '', '游客', '福建', '泉州', '218.5.148.195', '<img src=\\"/Public/Home/img/baidu/f2.png\\">\n                        ', 1461399835, 1, 0, 1461399955, 26),
	(83, 0, 'http://q.qlogo.cn/qqapp/101299164/33A82EAE60446EA22A4ACD6158DCF0E5/100', '指尖一缕年华', '四川', '成都', '175.152.118.123', 'http://www.dilidili.com/anime/kyojin/ 进击的巨人<br>', 1461648132, 1, 0, 1461648252, 2),
